#' @export
jamovimetamdiff <- function(
    switch = "from_raw",
    data,
    comparison_means,
    comparison_sds,
    comparison_ns,
    reference_means,
    reference_sds,
    reference_ns,
    r,
    labels,
    moderator,
    d,
    dcomparison_ns,
