#' Estimate Ind. 2x2
#'
#' 
#' @param data .
#' @param dep .
#' @param group1 .
#' @param group2 .
#' @param MEorInt .
#' @param conf.level .
#' @param show.mean.error .
#' @param show.raw.data .
#' @param ylab .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$means_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$contrast_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$interaction_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ME1_plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$ME2_plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$Int_plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$means_table$asDF}
#'
#' \code{as.data.frame(results$means_table)}
#'
#' @export
jmvComplexTwoByTwo <- function(
    data,
    dep,
    group1,
    group2,
    MEorInt = "fInt",
    conf.level = 95,
    show.mean.error = FALSE,
    show.raw.data = FALSE,
    ylab) {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvComplexTwoByTwo requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group1)) group1 <- jmvcore::resolveQuo(jmvcore::enquo(group1))
    if ( ! missing(group2)) group2 <- jmvcore::resolveQuo(jmvcore::enquo(group2))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group1), group1, NULL),
            `if`( ! missing(group2), group2, NULL))

    for (v in group1) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in group2) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jmvComplexTwoByTwoOptions$new(
        dep = dep,
        group1 = group1,
        group2 = group2,
        MEorInt = MEorInt,
        conf.level = conf.level,
        show.mean.error = show.mean.error,
        show.raw.data = show.raw.data,
        ylab = ylab)

    analysis <- jmvComplexTwoByTwoClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
