#' Meta-Analysis - r
#'
#' 
#' @param data .
#' @param labels .
#' @param r .
#' @param n .
#' @param moderator .
#' @param REorFE .
#' @param conf.level .
#' @param show.study.table .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$result_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$study_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$forest_plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$result_table$asDF}
#'
#' \code{as.data.frame(results$result_table)}
#'
#' @export
jmvMetaAnalysisR <- function(
    data,
    labels,
    r,
    n,
    moderator,
    REorFE = "RE",
    conf.level = 95,
    show.study.table = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvMetaAnalysisR requires jmvcore to be installed (restart may be required)')

    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if ( ! missing(r)) r <- jmvcore::resolveQuo(jmvcore::enquo(r))
    if ( ! missing(n)) n <- jmvcore::resolveQuo(jmvcore::enquo(n))
    if ( ! missing(moderator)) moderator <- jmvcore::resolveQuo(jmvcore::enquo(moderator))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(labels), labels, NULL),
            `if`( ! missing(r), r, NULL),
            `if`( ! missing(n), n, NULL),
            `if`( ! missing(moderator), moderator, NULL))


    options <- jmvMetaAnalysisROptions$new(
        labels = labels,
        r = r,
        n = n,
        moderator = moderator,
        REorFE = REorFE,
        conf.level = conf.level,
        show.study.table = show.study.table)

    analysis <- jmvMetaAnalysisRClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
