#'
#' \code{as.data.frame(results$result_table)}
#'
#' @export
jmvMetaAnalysisRaw <- function(
    data,
    labels,
    m1,
    s1,
    n1,
    m2,
    s2,
    n2,
    moderator,
    REorFE = "RE",
    g1label,
    g2label,
    conf.level = 95,
    report.cohens.d = FALSE,
    show.study.table = FALSE,
    explainDR = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvMetaAnalysisRaw requires jmvcore to be installed (restart may be required)')

    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if ( ! missing(m1)) m1 <- jmvcore::resolveQuo(jmvcore::enquo(m1))
    if ( ! missing(s1)) s1 <- jmvcore::resolveQuo(jmvcore::enquo(s1))
    if ( ! missing(n1)) n1 <- jmvcore::resolveQuo(jmvcore::enquo(n1))
    if ( ! missing(m2)) m2 <- jmvcore::resolveQuo(jmvcore::enquo(m2))
    if ( ! missing(s2)) s2 <- jmvcore::resolveQuo(jmvcore::enquo(s2))
    if ( ! missing(n2)) n2 <- jmvcore::resolveQuo(jmvcore::enquo(n2))
    if ( ! missing(moderator)) moderator <- jmvcore::resolveQuo(jmvcore::enquo(moderator))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(labels), labels, NULL),
            `if`( ! missing(m1), m1, NULL),
            `if`( ! missing(s1), s1, NULL),
            `if`( ! missing(n1), n1, NULL),
            `if`( ! missing(m2), m2, NULL),
            `if`( ! missing(s2), s2, NULL),
            `if`( ! missing(n2), n2, NULL),
            `if`( ! missing(moderator), moderator, NULL))


    options <- jmvMetaAnalysisRawOptions$new(
        labels = labels,
        m1 = m1,
        s1 = s1,
        n1 = n1,
        m2 = m2,
        s2 = s2,
        n2 = n2,
        moderator = moderator,
        REorFE = REorFE,
        g1label = g1label,
        g2label = g2label,
        conf.level = conf.level,
        report.cohens.d = report.cohens.d,
        show.study.table = show.study.table,
        explainDR = explainDR)

    analysis <- jmvMetaAnalysisRawClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
